// Parse Telegram command and route to appropriate workflow
const input = $input.first().json;
const text = input.message?.text || input.callback_query?.data || '';
const userId = input.message?.from?.id || input.callback_query?.from?.id;
const chatId = input.message?.chat?.id || input.callback_query?.message?.chat?.id;

// Security: Only allow specific users (replace with your Telegram user IDs)
const ALLOWED_USER_IDS = [123456789]; // Replace with your actual user ID
if (!ALLOWED_USER_IDS.includes(userId)) {
  return [{
    json: {
      error: true,
      chatId: chatId,
      message: 'ðŸ”’ Unauthorized access. This bot is for authorized users only.',
      userId: userId
    }
  }];
}

// Rate limiting check
const currentTime = Date.now();
const lastCommand = global.lastCommandTime || {};
if (lastCommand[userId] && (currentTime - lastCommand[userId]) < 1000) {
  return [{
    json: {
      error: true,
      chatId: chatId,
      message: 'â±ï¸ Please wait a moment before sending another command.',
      userId: userId
    }
  }];
}

global.lastCommandTime = global.lastCommandTime || {};
global.lastCommandTime[userId] = currentTime;

// Command routing logic
const command = text.split(' ')[0].toLowerCase();
const args = text.substring(command.length).trim();

let commandType = 'unknown';
let workflowTarget = '';
let helpText = '';

switch (command) {
  case '/start':
    commandType = 'welcome';
    helpText = `ðŸ¤– Welcome to Home Assistant Google API Bot!\n\nðŸ“§ Email Commands:\n/send_email email@example.com Subject: Your subject Body: Your message\n/gmail_check - Check recent emails\n\nðŸ“… Calendar Commands:\n/calendar Meeting Title | 2025-01-15 14:00 | 1 hour\n/calendar_today - Show today's events\n\nðŸ“ Drive Commands:\n/drive_upload - Reply with file attachment\n/drive_list - List recent files\n\nðŸ“ Docs Commands:\n/doc_create Title: Meeting Notes Body: Discussion content\n/doc_read document_id\n/doc_edit document_id Body: Updated content\n\nðŸ”§ Utility:\n/help - Show this help\n/status - Check API status\n\nSend any command to get started! ðŸš€`;
    break;

  case '/help':
    commandType = 'help';
    helpText = `ðŸ“š Available Commands:\n\nðŸ“§ EMAIL:\nâ€¢ /send_email recipient@example.com Subject: Title Body: Message\nâ€¢ /gmail_check - Show recent emails\n\nðŸ“… CALENDAR:\nâ€¢ /calendar Event Name | 2025-01-15 14:00 | 1 hour\nâ€¢ /calendar_today - Today's events\n\nðŸ“ DRIVE:\nâ€¢ /drive_upload - Upload file (reply with attachment)\nâ€¢ /drive_list - List recent files\n\nðŸ“ DOCUMENTS:\nâ€¢ /doc_create Title: Name Body: Content\nâ€¢ /doc_read document_id\nâ€¢ /doc_edit document_id Body: New content\n\nðŸ”§ UTILITY:\nâ€¢ /status - Check API connections\nâ€¢ /help - Show this help\n\nðŸ’¡ Tips:\n- Use exact format for commands\n- Wait for bot response before next command\n- File uploads: send /drive_upload then attach file`;
    break;

  case '/send_email':
    commandType = 'email';
    workflowTarget = 'gmail-send';
    break;

  case '/gmail_check':
    commandType = 'email';
    workflowTarget = 'gmail-check';
    break;

  case '/calendar':
    commandType = 'calendar';
    workflowTarget = 'calendar-create';
    break;

  case '/calendar_today':
    commandType = 'calendar';
    workflowTarget = 'calendar-today';
    break;

  case '/drive_upload':
    commandType = 'drive';
    workflowTarget = 'drive-upload';
    break;

  case '/drive_list':
    commandType = 'drive';
    workflowTarget = 'drive-list';
    break;

  case '/doc_create':
    commandType = 'docs';
    workflowTarget = 'docs-create';
    break;

  case '/doc_read':
    commandType = 'docs';
    workflowTarget = 'docs-read';
    break;

  case '/doc_edit':
    commandType = 'docs';
    workflowTarget = 'docs-edit';
    break;

  case '/status':
    commandType = 'status';
    helpText = 'ðŸ”§ Checking API connections...\n\nâœ… Telegram Bot: Connected\nâœ… n8n Workflows: Active\nðŸ”„ Checking Google APIs...';
    break;

  default:
    commandType = 'unknown';
    helpText = `â“ Unknown command: ${command}\n\nType /help to see available commands.`;
}

return [{
  json: {
    commandType: commandType,
    workflowTarget: workflowTarget,
    originalCommand: text,
    args: args,
    chatId: chatId,
    userId: userId,
    helpText: helpText,
    timestamp: new Date().toISOString()
  }
}];

// Check Google API status
const googleStatus = {
  gmail: 'Connected âœ…',
  calendar: 'Connected âœ…',
  drive: 'Connected âœ…',
  docs: 'Connected âœ…'
};

const statusMessage = `ðŸ”§ System Status Report\n\nðŸ¤– Telegram Bot: Active âœ…\nðŸ”„ n8n Workflows: Running âœ…\n\nðŸ“± Google API Status:\nðŸ“§ Gmail: ${googleStatus.gmail}\nðŸ“… Calendar: ${googleStatus.calendar}\nðŸ“ Drive: ${googleStatus.drive}\nðŸ“ Docs: ${googleStatus.docs}\n\nâš¡ Last Updated: ${new Date().toLocaleString()}\n\nðŸ’¡ All systems operational!`;

return [{
  json: {
    chatId: $input.first().json.chatId,
    statusMessage: statusMessage
  }
}];
