// Parse Telegram command: /create_event [title] | [date] [time] | [duration] | [location] | [description]
// Supports multiple formats:
// /create_event Meeting | 2024-01-15 14:30 | 60 | Conference Room | Team standup
// /create_event Meeting | tomorrow 2pm | 1 hour
// /create_event Quick call | today 3pm | 30 minutes

const text = $input.first().json.message.text;
const userId = $input.first().json.message.from.id;

// Security: Only allow specific Telegram user ID (replace with your ID)
const ALLOWED_USER_ID = 123456789; // Replace with your Telegram user ID
if (userId !== ALLOWED_USER_ID) {
  throw new Error('Unauthorized user');
}

// Remove command prefix and split by pipes
const commandText = text.replace('/create_event ', '').trim();
const parts = commandText.split('|').map(p => p.trim());

if (parts.length < 2) {
  throw new Error('Invalid format. Use: /create_event Title | Date Time | Duration | Location | Description');
}

const title = parts[0] || 'Untitled Event';
const dateTimeStr = parts[1] || '';
const durationStr = parts[2] || '60'; // Default 60 minutes
const location = parts[3] || '';
const description = parts[4] || '';

// Parse date and time
let startDateTime;
try {
  // Handle natural language dates
  if (dateTimeStr.toLowerCase().includes('today')) {
    const today = new Date();
    const timeMatch = dateTimeStr.match(/(\d{1,2})(am|pm)/i);
    if (timeMatch) {
      let hour = parseInt(timeMatch[1]);
      if (timeMatch[2].toLowerCase() === 'pm' && hour !== 12) hour += 12;
      if (timeMatch[2].toLowerCase() === 'am' && hour === 12) hour = 0;
      today.setHours(hour, 0, 0, 0);
      startDateTime = today;
    } else {
      throw new Error('Could not parse time from: ' + dateTimeStr);
    }
  } else if (dateTimeStr.toLowerCase().includes('tomorrow')) {
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    const timeMatch = dateTimeStr.match(/(\d{1,2})(am|pm)/i);
    if (timeMatch) {
      let hour = parseInt(timeMatch[1]);
      if (timeMatch[2].toLowerCase() === 'pm' && hour !== 12) hour += 12;
      if (timeMatch[2].toLowerCase() === 'am' && hour === 12) hour = 0;
      tomorrow.setHours(hour, 0, 0, 0);
      startDateTime = tomorrow;
    } else {
      throw new Error('Could not parse time from: ' + dateTimeStr);
    }
  } else {
    // Try to parse as ISO date or standard format
    // Support formats: YYYY-MM-DD HH:MM, YYYY-MM-DD HH:MM:SS
    startDateTime = new Date(dateTimeStr);
    if (isNaN(startDateTime.getTime())) {
      throw new Error('Invalid date format. Use: YYYY-MM-DD HH:MM or "today 2pm" or "tomorrow 3pm"');
    }
  }
} catch (error) {
  throw new Error('Date parsing failed: ' + error.message);
}

// Parse duration
let durationMinutes = 60; // Default
try {
  if (durationStr.toLowerCase().includes('hour')) {
    const hourMatch = durationStr.match(/(\d+)\s*hour/i);
    if (hourMatch) {
      durationMinutes = parseInt(hourMatch[1]) * 60;
    }
  } else if (durationStr.toLowerCase().includes('minute')) {
    const minuteMatch = durationStr.match(/(\d+)\s*minute/i);
    if (minuteMatch) {
      durationMinutes = parseInt(minuteMatch[1]);
    }
  } else {
    // Assume it's just a number in minutes
    durationMinutes = parseInt(durationStr) || 60;
  }
} catch (error) {
  durationMinutes = 60; // Default fallback
}

// Calculate end time
const endDateTime = new Date(startDateTime.getTime() + (durationMinutes * 60 * 1000));

// Validate that the event is not in the past
if (startDateTime < new Date()) {
  throw new Error('Cannot create events in the past. Please check your date and time.');
}

return {
  json: {
    title: title,
    start: {
      dateTime: startDateTime.toISOString(),
      timeZone: 'America/New_York'
    },
    end: {
      dateTime: endDateTime.toISOString(),
      timeZone: 'America/New_York'
    },
    location: location,
    description: description,
    originalCommand: text,
    userId: userId,
    duration: durationMinutes,
    parsedDateTime: {
      start: startDateTime.toLocaleString(),
      end: endDateTime.toLocaleString(),
      duration: durationMinutes + ' minutes'
    }
  }
};

// Format successful event creation response for Telegram
const input = $input.all();
const commandData = input[0].json;
const eventData = input[1].json;

// Extract event details
const eventId = eventData.id;
const eventLink = eventData.htmlLink;
const title = commandData.title;
const startTime = new Date(commandData.start.dateTime);
const endTime = new Date(commandData.end.dateTime);
const location = commandData.location;
const description = commandData.description;
const duration = commandData.duration;

// Format response message
let message = `‚úÖ **Calendar Event Created Successfully!**\n\n`;
message += `üìÖ **${title}**\n\n`;
message += `üóìÔ∏è **Date:** ${startTime.toLocaleDateString()}\n`;
message += `üïê **Time:** ${startTime.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})} - ${endTime.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}\n`;
message += `‚è±Ô∏è **Duration:** ${duration} minutes\n`;

if (location) {
  message += `üìç **Location:** ${location}\n`;
}

if (description) {
  const desc = description.length > 100 ? 
    description.substring(0, 100) + '...' : description;
  message += `üìù **Description:** ${desc}\n`;
}

message += `\nüîó **[View in Google Calendar](${eventLink})**\n\n`;

// Add quick actions footer
message += `üí° **Quick Actions:**\n`;
message += `‚Ä¢ /calendar_today - View today's events\n`;
message += `‚Ä¢ /create_event - Create another event\n`;
message += `‚Ä¢ /calendar_week - View this week's events\n\n`;

// Add event creation tips
message += `üìã **Event Creation Tips:**\n`;
message += `‚Ä¢ Use format: Title | Date Time | Duration | Location | Description\n`;
message += `‚Ä¢ Natural dates: "today 2pm", "tomorrow 3pm"\n`;
message += `‚Ä¢ Duration: "1 hour", "30 minutes", or just "60"\n`;
message += `‚Ä¢ All fields except title and date/time are optional\n`;

return {
  json: {
    message: message,
    eventCreated: true,
    eventId: eventId,
    eventLink: eventLink,
    eventDetails: {
      title: title,
      start: startTime.toISOString(),
      end: endTime.toISOString(),
      location: location,
      description: description,
      duration: duration
    }
  }
};
