// Extract command text for parsing
const text = testData.command;
const commandText = text.replace('/create_event ', '').trim();
const parts = commandText.split('|').map(p => p.trim());

const title = parts[0] || 'Test Event';
const dateTimeStr = parts[1] || '';
const durationStr = parts[2] || '60';
const location = parts[3] || '';
const description = parts[4] || '';

// Parse date and time (simplified for testing)
let startDateTime;
try {
  if (dateTimeStr.toLowerCase().includes('today')) {
    const today = new Date();
    today.setHours(15, 0, 0, 0); // Default to 3 PM for testing
    startDateTime = today;
  } else if (dateTimeStr.toLowerCase().includes('tomorrow')) {
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    tomorrow.setHours(14, 0, 0, 0); // Default to 2 PM for testing
    startDateTime = tomorrow;
  } else if (dateTimeStr.toLowerCase().includes('yesterday')) {
    const yesterday = new Date();
    yesterday.setDate(yesterday.getDate() - 1);
    yesterday.setHours(14, 0, 0, 0);
    startDateTime = yesterday;
  } else {
    startDateTime = new Date(dateTimeStr);
    if (isNaN(startDateTime.getTime())) {
      startDateTime = new Date(); // Default to now for invalid dates
      startDateTime.setHours(startDateTime.getHours() + 1);
    }
  }
} catch (error) {
  startDateTime = new Date();
  startDateTime.setHours(startDateTime.getHours() + 1);
}

// Parse duration
let durationMinutes = 60;
if (durationStr.toLowerCase().includes('hour')) {
  const hourMatch = durationStr.match(/(\d+)\s*hour/i);
  if (hourMatch) {
    durationMinutes = parseInt(hourMatch[1]) * 60;
  }
} else if (durationStr.toLowerCase().includes('minute')) {
  const minuteMatch = durationStr.match(/(\d+)\s*minute/i);
  if (minuteMatch) {
    durationMinutes = parseInt(minuteMatch[1]);
  }
} else {
  durationMinutes = parseInt(durationStr) || 60;
}

const endDateTime = new Date(startDateTime.getTime() + (durationMinutes * 60 * 1000));

return {
  json: {
    title: title,
    start: {
      dateTime: startDateTime.toISOString(),
      timeZone: 'America/New_York'
    },
    end: {
      dateTime: endDateTime.toISOString(),
      timeZone: 'America/New_York'
    },
    location: location,
    description: description,
    testType: testData.testType,
    originalCommand: testData.command,
    expectedResult: testData.expectedResult,
    validationData: {
      isPastEvent: startDateTime < new Date(),
      duration: durationMinutes,
      hasLocation: !!location,
      hasDescription: !!description
    }
  }
};

// Validate event creation test results
const input = $input.all();
const testResults = [];

input.forEach((item, index) => {
  const data = item.json;

  let testResult = {
    testCase: `Event Creation Test ${index + 1}`,
    testType: data.testType,
    command: data.originalCommand,
    expectedResult: data.expectedResult,
    status: 'unknown',
    timestamp: new Date().toISOString()
  };

  // Validate based on test type
  switch (data.testType) {
    case 'full_format':
      testResult.status = (
        data.title && 
        data.start && 
        data.end && 
        data.location && 
        data.description
      ) ? 'passed' : 'failed';
      testResult.message = testResult.status === 'passed' ? 
        'All event fields parsed correctly' : 'Missing required event fields';
      break;

    case 'natural_date':
      testResult.status = (
        data.title && 
        data.start && 
        !data.validationData.isPastEvent
      ) ? 'passed' : 'failed';
      testResult.message = testResult.status === 'passed' ? 
        'Natural language date parsed successfully' : 'Natural date parsing failed';
      break;

    case 'with_location':
      testResult.status = (
        data.title && 
        data.validationData.hasLocation
      ) ? 'passed' : 'failed';
      testResult.message = testResult.status === 'passed' ? 
        'Location field parsed correctly' : 'Location parsing failed';
      break;

    case 'duration_minutes':
      testResult.status = (
        data.validationData.duration > 0 && 
        data.validationData.duration !== 60
      ) ? 'passed' : 'failed';
      testResult.message = testResult.status === 'passed' ? 
        `Duration parsed as ${data.validationData.duration} minutes` : 'Duration parsing failed';
      break;

    case 'past_date_error':
      testResult.status = data.validationData.isPastEvent ? 'passed' : 'failed';
      testResult.message = testResult.status === 'passed' ? 
        'Past date correctly detected (would trigger error)' : 'Past date validation failed';
      break;

    case 'minimal_format':
      testResult.status = (
        data.title && 
        data.start && 
        !data.validationData.hasLocation && 
        !data.validationData.hasDescription
      ) ? 'passed' : 'failed';
      testResult.message = testResult.status === 'passed' ? 
        'Minimal event format parsed correctly' : 'Minimal format parsing failed';
      break;

    default:
      testResult.status = 'unknown';
      testResult.message = 'Unknown test type';
  }

  testResult.eventDetails = {
    title: data.title,
    startTime: new Date(data.start.dateTime).toLocaleString(),
    duration: data.validationData.duration + ' minutes',
    hasLocation: data.validationData.hasLocation,
    hasDescription: data.validationData.hasDescription,
    isPastEvent: data.validationData.isPastEvent
  };

  testResults.push(testResult);
});

