// Test workflow for Google Calendar integration
// This creates test data for calendar operations

const testOperations = [
  {
    operation: 'read_today',
    timeRange: 'today',
    command: '/calendar_today',
    expectedResult: 'Today\'s events retrieved'
  },
  {
    operation: 'read_week',
    timeRange: 'week',
    command: '/calendar_week',
    expectedResult: 'Week events retrieved'
  },
  {
    operation: 'read_upcoming',
    timeRange: 'upcoming',
    command: '/calendar_upcoming',
    expectedResult: 'Upcoming events retrieved'
  },
  {
    operation: 'read_custom',
    timeRange: 'custom',
    command: '/calendar_list 2024-01-15 to 2024-01-20',
    startDate: '2024-01-15T00:00:00.000Z',
    endDate: '2024-01-20T23:59:59.999Z',
    expectedResult: 'Custom range events retrieved'
  }
];

return testOperations.map(op => ({ json: op }));

// Validate calendar test results and generate report
const input = $input.all();
const testResults = [];

input.forEach((item, index) => {
  const node = item.node;
  const data = item.json;

  let testResult = {
    testCase: `Calendar Test ${index + 1}`,
    node: node,
    status: 'unknown',
    data: data,
    timestamp: new Date().toISOString()
  };

  // Determine test status based on node and data
  if (node === 'Test Calendar Read') {
    testResult.operation = data.operation || 'read';

    // Check if we got calendar data
    if (data.items !== undefined || Array.isArray(data)) {
      const events = Array.isArray(data) ? data : (data.items || []);
      testResult.status = 'passed';
      testResult.message = `Successfully retrieved ${events.length} calendar events`;
      testResult.eventCount = events.length;

      // Validate event structure
      if (events.length > 0) {
        const sampleEvent = events[0];
        const hasRequiredFields = sampleEvent.summary !== undefined && 
                                 sampleEvent.start !== undefined;
        if (hasRequiredFields) {
          testResult.message += ' with valid structure';
        } else {
          testResult.status = 'warning';
          testResult.message += ' but events missing required fields';
        }
      }
    } else {
      testResult.status = 'failed';
      testResult.message = 'No calendar events data returned';
      testResult.eventCount = 0;
    }
  }

  testResults.push(testResult);
});

// Generate summary
const summary = {
  totalTests: testResults.length,
  passed: testResults.filter(r => r.status === 'passed').length,
  failed: testResults.filter(r => r.status === 'failed').length,
  warnings: testResults.filter(r => r.status === 'warning').length,
  timestamp: new Date().toISOString(),
  results: testResults
};

return [{ json: summary }];
