// Validate test results and generate report
const input = $input.all();
const testResults = [];

input.forEach((item, index) => {
  const node = item.node;
  const data = item.json;

  let testResult = {
    testCase: `Test ${index + 1}`,
    node: node,
    status: 'unknown',
    data: data,
    timestamp: new Date().toISOString()
  };

  // Determine test status based on node and data
  if (node === 'Test Create Google Doc') {
    testResult.operation = 'create';
    testResult.status = data.documentId ? 'passed' : 'failed';
    testResult.message = data.documentId ? 
      `Document created successfully with ID: ${data.documentId}` : 
      'Failed to create document';
  } else if (node === 'Test Read Google Doc') {
    testResult.operation = 'read';
    testResult.status = data.content ? 'passed' : 'failed';
    testResult.message = data.content ? 
      `Document content retrieved (${data.content.length} characters)` : 
      'Failed to read document content';
  } else if (node === 'Test Edit Google Doc') {
    testResult.operation = 'edit';
    testResult.status = data.success !== false ? 'passed' : 'failed';
    testResult.message = data.success !== false ? 
      'Document updated successfully' : 
      'Failed to update document';
  }

  testResults.push(testResult);
});

// Generate summary
const summary = {
  totalTests: testResults.length,
  passed: testResults.filter(r => r.status === 'passed').length,
  failed: testResults.filter(r => r.status === 'failed').length,
  timestamp: new Date().toISOString(),
  results: testResults
};

return [{ json: summary }];

